package Character;

public class Apuntes_Unicode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		char Corazon;
		
		Corazon = 9825;
		
		System.out.println(Corazon);
		
		char Secuencias;
		
		Secuencias = '\'';
		
		// MUETSRA COMILLA SIMPLE
		System.out.println(Secuencias);
		
		// MUETSRA COMILLA DOBLE
		Secuencias = '\"';
		System.out.println(Secuencias);
		
		Secuencias = '\t';
		
		// TABULADOR
		System.out.println("1" + Secuencias + "2");
	}

}
