package Control_De_Flujo;

public class If4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 0;
		a = 10;
		
		if (a == 10) {
			
			System.out.println("if_1");}
		
		else{
			
			System.out.println("else if_1");
		}
		
		if (a == 10) {
			
			System.out.println("if_2");}
		
		System.out.println("Variable a = " + a);
	}
}
