package Pruebas;

public class Operaciones_Aritmeticas {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		/*
		 * Realiza la suma, diferencia, division y el producto entre estos
		 * dos numeros: 1234, 567.
		 */
		
		System.out.println("El Resultado De La Suma Es: " + (1234 + 567));
		System.out.println("El Resultado De La Diferencia Es: " + (1234 - 567));
		System.out.println("El Resultado De La Division Es: " + (1234 / 567));
		System.out.println("El Resultado Del Producto Es: " + (1234 * 567));

		
		
	}

}
